// Function to calculate the minimum number of inversions (mutations) required to transform one string into another
int calculate_mutation_distance(const string& s1, const string& s2) {
    int n = s1.size();
    vector<int> pos(n);
    unordered_map<char, vector<int>> char_positions;

    // Map characters of s2 to their positions
    for (int i = 0; i < n; i++) {
        char_positions[s2[i]].push_back(i);
    }

    // Map characters of s1 to the corresponding positions in s2
    for (int i = 0; i < n; i++) {
        pos[i] = char_positions[s1[i]].back();
        char_positions[s1[i]].pop_back();
    }

    // Count inversions in pos, which is the minimum number of swaps needed
    vector<int> bit(n + 1, 0);

    auto add = [&](int idx, int val) {
        for (; idx <= n; idx += idx & -idx) {
            bit[idx] += val;
        }
    };

    auto sum = [&](int idx) {
        int s = 0;
        for (; idx > 0; idx -= idx & -idx) {
            s += bit[idx];
        }
        return s;
    };

    int inversions = 0;
    for (int i = n - 1; i >= 0; i--) {
        inversions += sum(pos[i]);
        add(pos[i] + 1, 1);
    }

    return inversions;
}

