    long long maxLocalCongestion = 0;
    for (int neighbor : adj[node]) {
        if (neighbor != parent) {
            long long fansThroughEdge = subtreePop[neighbor];
            maxLocalCongestion = max(maxLocalCongestion, fansThroughEdge);
        }
    }
    if (parent != -1) {
        long long fansThroughEdge = totalFans - subtreePop[node];
        maxLocalCongestion = max(maxLocalCongestion, fansThroughEdge);
    }
    maxCongestion[node] = maxLocalCongestion;

    for (int neighbor : adj[node]) {
        if (neighbor != parent) {
            dfsCalculateCongestion(neighbor, node, adj, P, subtreePop, totalFans, maxCongestion);
        }
    }
}

