// Function to locate the best city for the hockey arena
int LocateCentre(int N, vector<int> P, vector<int> S, vector<int> D) {
    // Step 1: Build the adjacency list representation of the tree
    vector<vector<int>> adj(N);
    for (int i = 0; i < N - 1; i++) {
        adj[S[i]].push_back(D[i]);
        adj[D[i]].push_back(S[i]);
    }

    // Step 2: Calculate the total population in the entire tree
    long long total_population = 0;
    for (int i = 0; i < N; i++) {
        total_population += P[i];
    }

    // Step 3: Use DFS to calculate subtree populations and congestion values
    vector<long long> subtree_population(N, 0);
    vector<long long> max_congestion(N, LLONG_MAX);

    // Helper function to compute subtree populations
    function<void(int, int)> calculate_subtree_population = [&](int node, int parent) {
        subtree_population[node] = P[node];
        for (int neighbor : adj[node]) {
            if (neighbor == parent) continue;
            calculate_subtree_population(neighbor, node);
            subtree_population[node] += subtree_population[neighbor];
        }
    };

    // Helper function to calculate maximum congestion for each city
    function<void(int, int, long long)> calculate_congestion = [&](int node, int parent, long long parent_congestion) {
        long long worst_congestion = parent_congestion;
        for (int neighbor : adj[node]) {
            if (neighbor == parent) continue;
            long long congestion = subtree_population[neighbor];
            worst_congestion = max(worst_congestion, congestion);
        }
        long long remaining_population = total_population - subtree_population[node];
        worst_congestion = max(worst_congestion, remaining_population);
        max_congestion[node] = worst_congestion;

        for (int neighbor : adj[node]) {
            if (neighbor == parent) continue;
            long long new_parent_congestion = total_population - subtree_population[neighbor];
            calculate_congestion(neighbor, node, new_parent_congestion);
        }
    };

    // Step 4: Perform the two DFS traversals
    calculate_subtree_population(0, -1); // First DFS to calculate subtree populations
    calculate_congestion(0, -1, 0);      // Second DFS to calculate maximum congestion

    // Step 5: Find the city with the minimum congestion value
    int best_city = 0;
    long long min_congestion = LLONG_MAX;
    for (int i = 0; i < N; i++) {
        if (max_congestion[i] < min_congestion) {
            min_congestion = max_congestion[i];
            best_city = i;
        }
    }

    return best_city;
}

