void LocateCentre(int N, vector<int> P, vector<int> S, vector<int> D, int &result) {
    // Tree representation
    vector<vector<int>> tree(N);
    for (int i = 0; i < N - 1; ++i) {
        tree[S[i]].push_back(D[i]);
        tree[D[i]].push_back(S[i]);
    }

    // Subtree fan counts
    vector<long long> subtreeFans(N, 0);
    vector<long long> maxSubtreeCongestion(N, 0);
    vector<bool> visited(N, false);
    long long totalFans = 0;

    // First DFS: Calculate subtree fan counts
    function<void(int)> dfs1 = [&](int node) {
        visited[node] = true;
        subtreeFans[node] = P[node];
        for (int neighbor : tree[node]) {
            if (!visited[neighbor]) {
                dfs1(neighbor);
                subtreeFans[node] += subtreeFans[neighbor];
            }
        }
    };

    dfs1(0);
    totalFans = subtreeFans[0]; // Total fans in the entire tree

    // Second DFS: Calculate max congestion for each node
    visited.assign(N, false);

    function<void(int)> dfs2 = [&](int node) {
        visited[node] = true;
        long long maxCongestion = 0;
        for (int neighbor : tree[node]) {
            if (!visited[neighbor]) {
                dfs2(neighbor);
                maxCongestion = max(maxCongestion, subtreeFans[neighbor]);
            }
        }
        // Congestion from the rest of the tree
        maxCongestion = max(maxCongestion, totalFans - subtreeFans[node]);
        maxSubtreeCongestion[node] = maxCongestion;
    };

    dfs2(0);

    // Find the node with the smallest maximum congestion
    long long minCongestion = LLONG_MAX;
    for (int i = 0; i < N; ++i) {
        if (maxSubtreeCongestion[i] < minCongestion) {
            minCongestion = maxSubtreeCongestion[i];
            result = i;
        }
    }
}

