    for (int neighbor : tree[node]) {
        if (neighbor != parent) {
            long long fans_through_road = subtree_fans[neighbor];
            max_road_congestion = max(max_road_congestion, fans_through_road);
        }
    }

    // Consider fans outside the current node's subtree
    if (parent != -1) {
        long long fans_through_road = total_fans - subtree_fans[node];
        max_road_congestion = max(max_road_congestion, fans_through_road);
    }

    max_congestion[node] = max_road_congestion;

    // Re-root the tree to each child and calculate congestion
    for (int neighbor : tree[node]) {
        if (neighbor != parent) {
            dfs_reroot(neighbor, node, tree, P, subtree_fans, max_congestion, total_fans);
        }
    }
}

int LocateCentre(int N, vector<int> P, vector<int> S, vector<int> D) {
    // Build the tree as an adjacency list
    vector<vector<int>> tree(N);
    for (int i = 0; i < N - 1; i++) {
        tree[S[i]].push_back(D[i]);
        tree[D[i]].push_back(S[i]);
    }

    // Step 1: Calculate subtree fans using DFS
    vector<long long> subtree_fans(N, 0);
    dfs_subtree(0, -1, tree, P, subtree_fans);

    // Step 2: Calculate maximum congestion for each city
    vector<long long> max_congestion(N, LLONG_MAX);
    long long total_fans = subtree_fans[0]; // Total fans in the entire tree
    dfs_reroot(0, -1, tree, P, subtree_fans, max_congestion, total_fans);

    // Step 3: Find the city with the minimum maximum congestion
    int best_city = 0;
    long long min_max_congestion = LLONG_MAX;
    for (int i = 0; i < N; i++) {
        if (max_congestion[i] < min_max_congestion) {
            min_max_congestion = max_congestion[i];
            best_city = i;
        }
    }

    return best_city;
}

