// Helper function to compute prefix sum matrix
vector<vector<int>> computePrefixSum(const vector<vector<int>>& grid, int R, int C) {
    vector<vector<int>> prefix(R + 1, vector<int>(C + 1, 0));
    for (int i = 1; i <= R; ++i) {
        for (int j = 1; j <= C; ++j) {
            prefix[i][j] = grid[i - 1][j - 1] 
                         + prefix[i - 1][j] 
                         + prefix[i][j - 1] 
                         - prefix[i - 1][j - 1];
        }
    }
    return prefix;
}

// Check if a given value can be the median
bool canBeMedian(int R, int C, int H, int W, const vector<vector<int>>& Q, int candidate) {
    // Create a binary grid where 1 means <= candidate, 0 otherwise
    vector<vector<int>> binaryGrid(R, vector<int>(C, 0));
    for (int i = 0; i < R; ++i) {
        for (int j = 0; j < C; ++j) {
            binaryGrid[i][j] = (Q[i][j] <= candidate) ? 1 : 0;
        }
    }

    // Compute prefix sum for the binary grid
    auto prefix = computePrefixSum(binaryGrid, R, C);

    // Total number of cells in an H x W subgrid
    int totalCells = H * W;
    int requiredOnes = (totalCells / 2) + 1; // Minimum number of 1's needed for candidate to be the median

    // Check all possible H x W subgrids
    for (int i = H; i <= R; ++i) {
        for (int j = W; j <= C; ++j) {
            int sum = prefix[i][j]
                    - prefix[i - H][j]
                    - prefix[i][j - W]
                    + prefix[i - H][j - W];
            if (sum >= requiredOnes) {
                return true; // Found a valid subgrid
            }
        }
    }

    return false; // No valid subgrid found
}

// Main function to find the smallest possible median
int rectangle(int R, int C, int H, int W, vector<vector<int>> Q) {
    // Flatten the grid to get all possible quality ranks
    vector<int> allRanks;
    for (const auto& row : Q) {
        for (int val : row) {
            allRanks.push_back(val);
        }
    }

    // Sort the ranks for binary search
    sort(allRanks.begin(), allRanks.end());

    int left = 0, right = allRanks.size() - 1;
    int bestMedian = allRanks[right];

    // Binary search for the smallest feasible median
    while (left <= right) {
        int mid = left + (right - left) / 2;
        int candidate = allRanks[mid];

        if (canBeMedian(R, C, H, W, Q, candidate)) {
            bestMedian = candidate; // Update best median
            right = mid - 1;        // Try smaller medians
        } else {
            left = mid + 1;         // Try larger medians
        }
    }

    return bestMedian;
}

