vector<int> ucs(vector<int> A, vector<int> B) {
    int N = A.size();
    int M = B.size();

    // Step 1: Compute the DP table for LCS
    vector<vector<int>> dp(N + 1, vector<int>(M + 1, 0));

    for (int i = 1; i <= N; ++i) {
        for (int j = 1; j <= M; ++j) {
            if (A[i - 1] == B[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1] + 1;
            } else {
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1]);
            }
        }
    }

    // Step 2: Reconstruct the LCS and check for uniqueness
    vector<int> lcs;
    int i = N, j = M;

    while (i > 0 && j > 0) {
        if (A[i - 1] == B[j - 1]) {
            lcs.push_back(A[i - 1]);
            --i;
            --j;
        } else if (dp[i - 1][j] > dp[i][j - 1]) {
            --i;
        } else if (dp[i - 1][j] < dp[i][j - 1]) {
            --j;
        } else {
            // Multiple paths exist, meaning the LCS is not unique
            return {-1};
        }
    }

    // Since we constructed the LCS in reverse order, reverse it back
    reverse(lcs.begin(), lcs.end());

    return lcs;
}

