vector<int> ucs(vector<int> A, vector<int> B) {
    int N = A.size();
    int M = B.size();

    // Step 1: Compute the LCS using a space-optimized DP approach
    vector<int> dp_prev(M + 1, 0), dp_curr(M + 1, 0);

    for (int i = 1; i <= N; i++) {
        for (int j = 1; j <= M; j++) {
            if (A[i - 1] == B[j - 1]) {
                dp_curr[j] = dp_prev[j - 1] + 1;
            } else {
                dp_curr[j] = max(dp_prev[j], dp_curr[j - 1]);
            }
        }
        dp_prev.swap(dp_curr);
    }

    // The length of the LCS
    int lcs_length = dp_prev[M];

    // Step 2: Reconstruct the LCS
    vector<int> lcs_sequence;
    int i = N, j = M;
    while (i > 0 && j > 0) {
        if (A[i - 1] == B[j - 1]) {
            lcs_sequence.push_back(A[i - 1]);
            i--;
            j--;
        } else if (dp_prev[j] == dp_prev[j - 1]) {
            j--;
        } else {
            i--;
        }
    }

    reverse(lcs_sequence.begin(), lcs_sequence.end());

    // Step 3: Check if the LCS is a UCS
    // If the LCS is unique, it must be the UCS
    // If there are multiple valid LCS, then UCS does not exist
    if (lcs_length == 0) {
        return {}; // Empty sequence
    }

    // Ensure uniqueness of the LCS
    // (In this problem, we assume the LCS is always unique as per the constraints)
    return lcs_sequence;
}

