        if (A[i - 1] == B[j - 1]) {
            lcs.push_back(A[i - 1]);
            --i;
            --j;
        } else if (dp[i - 1][j] > dp[i][j - 1]) {
            --i;
        } else {
            --j;
        }
    }

    // The LCS is built in reverse order, reverse it to get the correct sequence
    std::reverse(lcs.begin(), lcs.end());

    // Step 3: Check if the LCS is a universal common subsequence
    // If LCS is empty, no UCS exists
    if (lcs.empty()) {
        return {};
    }

    // The LCS is the UCS if all common subsequences of A and B are subsequences of the LCS.
