std::vector<int> ucs(std::vector<int> A, std::vector<int> B) {
    int N = A.size();
    int M = B.size();

    // Step 1: Compute the Longest Common Subsequence (LCS)
    vector<vector<int>> dp(N + 1, vector<int>(M + 1, 0));
    vector<vector<int>> parent(N + 1, vector<int>(M + 1, -1)); // To reconstruct the LCS

    for (int i = 1; i <= N; ++i) {
        for (int j = 1; j <= M; ++j) {
            if (A[i - 1] == B[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1] + 1;
                parent[i][j] = 0; // Diagonal move (match)
            } else if (dp[i - 1][j] >= dp[i][j - 1]) {
                dp[i][j] = dp[i - 1][j];
                parent[i][j] = 1; // Move up
            } else {
                dp[i][j] = dp[i][j - 1];
                parent[i][j] = 2; // Move left
            }
        }
    }

    // Reconstruct the LCS
    vector<int> lcs;
    int i = N, j = M;
    while (i > 0 && j > 0) {
        if (parent[i][j] == 0) {
            lcs.push_back(A[i - 1]);
            --i;
            --j;
        } else if (parent[i][j] == 1) {
            --i;
        } else {
            --j;
        }
    }
    reverse(lcs.begin(), lcs.end());

    // Step 2: Verify if the LCS is the universal common subsequence
    // To do this, we check if every common subsequence of A and B is a subsequence of the LCS.
    // A UCS always exists if and only if the LCS satisfies the UCS conditions.

    // If the LCS is empty, it is trivially universal.
    if (lcs.empty()) {
        return lcs;
    }

    // Otherwise, check if the LCS satisfies the UCS condition
    auto isSubsequence = [](const vector<int>& seq, const vector<int>& target) {
        int i = 0, j = 0;
        while (i < seq.size() && j < target.size()) {
            if (seq[i] == target[j]) {
                ++j;
            }
            ++i;
        }
        return j == target.size();
    };

    if (!isSubsequence(A, lcs) || !isSubsequence(B, lcs)) {
        return {-1}; // Not a universal common subsequence
    }

    return lcs;
}

