long long int findMaxAttraction(int n, int start, int d, int attraction[]) {
    // Edge case: If there are no days, no attractions can be visited
    if (d == 0) return 0;

    // Use two arrays for DP to save space (current and previous days)
    vector<long long> prev(n, LLONG_MIN), curr(n, LLONG_MIN);

    // Base case: On day 0, Jian-Jia is at the starting city
    prev[start] = attraction[start];

    // Iterate over each day
    for (int day = 1; day <= d; ++day) {
        for (int city = 0; city < n; ++city) {
            // Reset current day's value for this city
            curr[city] = LLONG_MIN;

            // Option 1: Stay and visit attractions in the current city
            if (prev[city] != LLONG_MIN) {
                curr[city] = max(curr[city], prev[city] + attraction[city]);
            }

            // Option 2: Move from an adjacent city
            if (city > 0 && prev[city - 1] != LLONG_MIN) {
                curr[city] = max(curr[city], prev[city - 1]);
            }
            if (city < n - 1 && prev[city + 1] != LLONG_MIN) {
                curr[city] = max(curr[city], prev[city + 1]);
            }
        }

        // Swap current and previous day arrays for the next iteration
        swap(prev, curr);
    }

    // The maximum attractions visited after 'd' days
    long long maxAttractions = 0;
    for (int city = 0; city < n; ++city) {
        maxAttractions = max(maxAttractions, prev[city]);
    }

    return maxAttractions;
}

