long long int findMaxAttraction(int n, int start, int d, int attraction[]) {
    // DP table: dp[day][city] stores the maximum attractions Jian-Jia can visit
    // if he ends up at `city` on `day`.
    vector<vector<long long>> dp(d + 1, vector<long long>(n, -1));

    // Base case: On day 0, Jian-Jia is at the starting city but hasn't visited anything yet.
    dp[0][start] = 0;

    // Fill the DP table
    for (int day = 1; day <= d; ++day) {
        for (int city = 0; city < n; ++city) {
            // If it's possible to be at this city on the current day
            if (dp[day - 1][city] != -1) {
                // Option 1: Stay in the same city and visit attractions (if not visited yet)
                dp[day][city] = max(dp[day][city], dp[day - 1][city] + attraction[city]);
                // Set attractions to 0 for the city, as they can't be visited again
                attraction[city] = 0;

                // Option 2: Move to adjacent cities
                if (city > 0) {
                    dp[day][city - 1] = max(dp[day][city - 1], dp[day - 1][city]);
                }
                if (city < n - 1) {
                    dp[day][city + 1] = max(dp[day][city + 1], dp[day - 1][city]);
                }
            }
        }
    }

    // Find the maximum attractions Jian-Jia can visit in `d` days
    long long maxAttractions = 0;
    for (int city = 0; city < n; ++city) {
        maxAttractions = max(maxAttractions, dp[d][city]);
    }

    return maxAttractions;
}

