    // DP table: dp[days][city] - max attractions Jian-Jia can visit in `days` days ending at `city`
    vector<vector<long long>> dp(d + 1, vector<long long>(n, 0));

    // Base case: On day 0, no attractions are visited
    for (int i = 0; i < n; ++i) {
        dp[0][i] = 0;
    }

    // Process each day
    for (int day = 1; day <= d; ++day) {
        for (int city = 0; city < n; ++city) {
            // Option 1: Stay at the current city and visit attractions
            dp[day][city] = dp[day - 1][city] + attraction[city];

            // Option 2: Move to an adjacent city
            if (city > 0) {
                dp[day][city] = max(dp[day][city], dp[day - 1][city - 1]);
            }
            if (city < n - 1) {
                dp[day][city] = max(dp[day][city], dp[day - 1][city + 1]);
            }
        }
    }

    // Find the maximum attractions visited within `d` days starting from `start`
    long long maxAttractions = 0;
    for (int city = 0; city < n; ++city) {
        maxAttractions = max(maxAttractions, dp[d][city]);
    }

    return maxAttractions;
}

