int64_t take_photos(int n, int m, int k, vector<int> r, vector<int> c) {
    // Step 1: Group points by their diagonal distance
    vector<pair<int, int>> points(n);
    for (int i = 0; i < n; ++i) {
        points[i] = {r[i], c[i]};
    }
    sort(points.begin(), points.end(), [&](pair<int, int> a, pair<int, int> b) {
        int d1 = a.first - a.second;
        int d2 = b.first - b.second;
        return d1 < d2 || (d1 == d2 && a.first < b.first);
    });

    // Step 2: Compress diagonal groups
    vector<pair<int, int>> groups;
    int current_diag = points[0].first - points[0].second;
    int r_min = points[0].first, r_max = points[0].first;
    for (auto& p : points) {
        int diag = p.first - p.second;
        if (diag != current_diag) {
            groups.push_back({r_min, r_max});
            current_diag = diag;
            r_min = p.first;
            r_max = p.first;
        } else {
            r_min = min(r_min, p.first);
            r_max = max(r_max, p.first);
        }
    }
    groups.push_back({r_min, r_max});

    int g = groups.size();

    // Step 3: DP to minimize photographed cells
    vector<vector<int64_t>> dp(k + 1, vector<int64_t>(g + 1, INT64_MAX));
    dp[0][0] = 0;

    for (int i = 1; i <= k; ++i) {
        for (int j = 1; j <= g; ++j) {
            int r_min = groups[j - 1].first;
            int r_max = groups[j - 1].second;
            int64_t side_length = r_max - r_min + 1;
            int64_t cost = side_length * side_length;

            // Try covering groups [l, j] with one square
            for (int l = j; l >= 1; --l) {
                r_min = min(r_min, groups[l - 1].first);
                r_max = max(r_max, groups[l - 1].second);
                side_length = r_max - r_min + 1;
                cost = side_length * side_length;

                dp[i][j] = min(dp[i][j], dp[i - 1][l - 1] + cost);
            }
        }
    }

    // Step 4: Return the minimum cost to cover all groups with up to k squares
    return dp[k][g];
}

