// Helper function to calculate the number of cells in a square area
inline int64_t cells_in_square(int side_length) {
    return static_cast<int64_t>(side_length) * side_length;
}

int64_t take_photos(int n, int m, int k, vector<int> r, vector<int> c) {
    // Collect the points of interest and sort them by their diagonal position
    vector<int> diagonals(n);
    for (int i = 0; i < n; i++) {
        diagonals[i] = r[i] + c[i]; // Calculate the diagonal position (r + c)
    }
    sort(diagonals.begin(), diagonals.end());

    // Use binary search to find the minimum possible number of cells
    int64_t low = 1, high = cells_in_square(m), result = LLONG_MAX;

    while (low <= high) {
        int64_t mid = (low + high) / 2;

        // Try to cover all points with at most k squares of size mid x mid
        int covered = 0, photos = 0;
        for (int i = 0; i < n;) {
            int start = diagonals[i];
            photos++;
            covered = start + mid - 1;

            // Skip all points that are covered by the current square
            while (i < n && diagonals[i] <= covered) {
                i++;
            }
        }

        if (photos <= k) {
            result = mid;
            high = mid - 1; // Try a smaller square size
        } else {
            low = mid + 1; // Try a larger square size
        }
    }

    return cells_in_square(result);
}

