int64_t take_photos(int n, int m, int k, vector<int> r, vector<int> c) {
    // Combine rows and columns into a single array of diagonal indices
    vector<int> diag_indices(n);
    for (int i = 0; i < n; ++i) {
        diag_indices[i] = r[i] + c[i];
    }

    // Sort points by their diagonal index
    sort(diag_indices.begin(), diag_indices.end());

    // Precompute costs of photographing subsets of points
    vector<vector<int64_t>> cost(n, vector<int64_t>(n, LLONG_MAX));
    for (int i = 0; i < n; ++i) {
        int min_diag = diag_indices[i];
        int max_diag = diag_indices[i];
        for (int j = i; j < n; ++j) {
            min_diag = min(min_diag, diag_indices[j]);
            max_diag = max(max_diag, diag_indices[j]);
            int size = max_diag - min_diag + 1;
            cost[i][j] = 1LL * size * size; // Area of the square
        }
    }

    // DP table
    vector<vector<int64_t>> dp(n + 1, vector<int64_t>(k + 1, LLONG_MAX));
    dp[0][0] = 0;

    // Fill DP table
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= k; ++j) {
            for (int p = 0; p < i; ++p) {
                dp[i][j] = min(dp[i][j], dp[p][j - 1] + cost[p][i - 1]);
            }
        }
    }

    // Return the minimum cost to cover all points using at most k photos
    return dp[n][k];
}

