int64_t take_photos(int n, int m, int k, vector<int> r, vector<int> c) {
    // Precompute diagonal positions
    vector<int> d(n);
    for (int i = 0; i < n; ++i) {
        d[i] = r[i] - c[i];
    }

    // Sort points by their diagonal positions
    vector<int> indices(n);
    for (int i = 0; i < n; ++i) indices[i] = i;
    sort(indices.begin(), indices.end(), [&](int i, int j) {
        return d[i] < d[j];
    });

    // After sorting, calculate the minimum and maximum row/column for each group
    vector<int> sorted_r(n), sorted_c(n);
    for (int i = 0; i < n; ++i) {
        sorted_r[i] = r[indices[i]];
        sorted_c[i] = c[indices[i]];
    }

    // Precompute cost to cover points [i, j] with one photo
    vector<vector<int64_t>> cost(n, vector<int64_t>(n, LLONG_MAX));
    for (int i = 0; i < n; ++i) {
        int min_r = sorted_r[i], max_r = sorted_r[i];
        int min_c = sorted_c[i], max_c = sorted_c[i];
        for (int j = i; j < n; ++j) {
            min_r = min(min_r, sorted_r[j]);
            max_r = max(max_r, sorted_r[j]);
            min_c = min(min_c, sorted_c[j]);
            max_c = max(max_c, sorted_c[j]);

            // The side length of the square covering [i, j]
            int side_length = max(max_r - min_r, max_c - min_c) + 1;
            cost[i][j] = int64_t(side_length) * side_length;
        }
    }

    // DP to minimize the total photographed cells with at most k photos
    vector<vector<int64_t>> dp(k + 1, vector<int64_t>(n + 1, LLONG_MAX));
    dp[0][0] = 0;
    for (int photos = 1; photos <= k; ++photos) {
        for (int i = 0; i < n; ++i) {
            for (int j = i; j < n; ++j) {
                dp[photos][j + 1] = min(dp[photos][j + 1], dp[photos - 1][i] + cost[i][j]);
            }
        }
    }

    // Return the minimum cost to cover all points with at most k photos
    return dp[k][n];
}

