vector<int> distribute_candies(vector<int> c, vector<int> l, vector<int> r, vector<int> v) {
    int n = c.size();
    int q = l.size();

    // Initialize the current state of candies in each box to 0
    vector<int> candies(n, 0);

    // Process each day's operation
    for (int j = 0; j < q; ++j) {
        int left = l[j];
        int right = r[j];
        int value = v[j];

        if (value > 0) {
            // Add candies to the boxes
            for (int k = left; k <= right; ++k) {
                int remaining_capacity = c[k] - candies[k];
                int to_add = min(remaining_capacity, value);
                candies[k] += to_add;
                value -= to_add;

                // If no more candies to add, break early
                if (value == 0) break;
            }
        } else if (value < 0) {
            value = -value; // Convert to positive for easier processing
            // Remove candies from the boxes
            for (int k = left; k <= right; ++k) {
                int to_remove = min(candies[k], value);
                candies[k] -= to_remove;
                value -= to_remove;

                // If no more candies to remove, break early
                if (value == 0) break;
            }
        }
    }

    return candies;
}

