        } else {
            // If flipping the switch changes the result, it means the correct position
            // is the flipped state. Record the correct position and door.
            finalS[i] = 1; // Correct position is 1
            D[i] = flippedResult; // This switch is connected to the door indicated by the flipped result
        }

        // Restore the current switch to its correct position for subsequent tests
        S[i] = finalS[i];
