vector<int> find_subset(int l, int u, vector<int> w) {
    int n = w.size();

    // Create a vector of pairs to store weights and their original indices
    vector<pair<int, int>> indexed_weights;
    for (int i = 0; i < n; i++) {
        indexed_weights.push_back({w[i], i});
    }

    // Sort the weights along with their indices in ascending order
    sort(indexed_weights.begin(), indexed_weights.end());

    // Prefix sum array to efficiently calculate sums of subsets
    vector<int> prefix_sum(n + 1, 0);
    for (int i = 0; i < n; i++) {
        prefix_sum[i + 1] = prefix_sum[i] + indexed_weights[i].first;
    }

    // Find a subset with a total weight in the range [l, u]
    for (int i = 0; i < n; i++) {
        for (int j = i; j < n; j++) {
            int current_sum = prefix_sum[j + 1] - prefix_sum[i];
            if (current_sum >= l && current_sum <= u) {
                // Found a valid subset, extract the indices
                vector<int> result;
                for (int k = i; k <= j; k++) {
                    result.push_back(indexed_weights[k].second);
                }
                return result;
            }
        }
    }

    // If no subset is found, return an empty array
    return {};
}

