        prefix_sum[i + 1] = prefix_sum[i] + indexed_weights[i].first;
    }

    // Try to find a valid subset
    for (int i = 0; i < w.size(); i++) {
        for (int j = i + 1; j <= w.size(); j++) {
            int subset_sum = prefix_sum[j] - prefix_sum[i];
            if (subset_sum >= l && subset_sum <= u) {
                // Found a valid subset, collect its indices
                vector<int> result;
                for (int k = i; k < j; k++) {
                    result.push_back(indexed_weights[k].second);
                }
                return result; // Return the valid subset
            }
        }
    }

    // No subset found
    return {};
}

