// Function to perform DFS and find connected components
void dfs(int node, int component_id, vector<int>& component, const vector<vector<int>>& adj, vector<bool>& visited) {
    visited[node] = true;
    component[node] = component_id;
    for (int neighbor : adj[node]) {
        if (!visited[neighbor]) {
            dfs(neighbor, component_id, component, adj, visited);
        }
    }
}

vector<int> find_split(int n, int a, int b, int c, vector<int> p, vector<int> q) {
    // Step 1: Build the adjacency list
    vector<vector<int>> adj(n);
    for (size_t i = 0; i < p.size(); i++) {
        adj[p[i]].push_back(q[i]);
        adj[q[i]].push_back(p[i]);
    }

    // Step 2: Find connected components
    vector<int> component(n, -1);
    vector<bool> visited(n, false);
    int component_id = 0;
    for (int i = 0; i < n; i++) {
        if (!visited[i]) {
            dfs(i, component_id++, component, adj, visited);
        }
    }

    // Group nodes by their component
    vector<vector<int>> components(component_id);
    for (int i = 0; i < n; i++) {
        components[component[i]].push_back(i);
    }

    // Step 3: Dynamic programming to try to partition components
    vector<int> result(n, 0);

    // Sort components by size
    vector<pair<int, int>> component_sizes;
    for (int i = 0; i < component_id; i++) {
        component_sizes.push_back({components[i].size(), i});
    }
    sort(component_sizes.begin(), component_sizes.end());

    // Try to assign nodes to sets A, B, and C
    vector<vector<int>> dp(component_id + 1, vector<int>(a + 1, -1));
    dp[0][0] = 0;

    for (int i = 0; i < component_id; i++) {
        int size = component_sizes[i].first;
        for (int j = 0; j <= a; j++) {
            if (dp[i][j] != -1) {
                // Do not include this component in set A
                dp[i + 1][j] = dp[i][j];
                // Include this component in set A if it fits
                if (j + size <= a) {
                    dp[i + 1][j + size] = i;
                }
            }
        }
    }

    if (dp[component_id][a] == -1) {
        return vector<int>(n, 0); // No valid partition
    }

    // Recover the partition
    int remaining_a = a;
    for (int i = component_id; i > 0; i--) {
        if (dp[i][remaining_a] != dp[i - 1][remaining_a]) {
            int comp_idx = component_sizes[i - 1].second;
            for (int node : components[comp_idx]) {
                result[node] = 1; // Assign to set A
            }
            remaining_a -= components[comp_idx].size();
        }
    }

    // Assign remaining components to B and C
    int remaining_b = b;
    for (int i = 0; i < component_id; i++) {
        int comp_idx = component_sizes[i].second;
        if (result[components[comp_idx][0]] == 0) {
            if (remaining_b >= components[comp_idx].size()) {
                for (int node : components[comp_idx]) {
                    result[node] = 2; // Assign to set B
                }
                remaining_b -= components[comp_idx].size();
            } else {
                for (int node : components[comp_idx]) {
                    result[node] = 3; // Assign to set C
                }
            }
        }
    }

    return result;
}

