// Helper function to perform BFS and find connected components
void bfs(int start, vector<int>& component, vector<bool>& visited, const vector<vector<int>>& adj) {
    queue<int> q;
    q.push(start);
    visited[start] = true;

    while (!q.empty()) {
        int node = q.front();
        q.pop();
        component.push_back(node);

        for (int neighbor : adj[node]) {
            if (!visited[neighbor]) {
                visited[neighbor] = true;
                q.push(neighbor);
            }
        }
    }
}

vector<int> find_split(int n, int a, int b, int c, vector<int> p, vector<int> q) {
    vector<vector<int>> adj(n); // Adjacency list
    for (int i = 0; i < p.size(); ++i) {
        adj[p[i]].push_back(q[i]);
        adj[q[i]].push_back(p[i]);
    }

    // Step 1: Find connected components
    vector<bool> visited(n, false);
    vector<vector<int>> components; // List of connected components

    for (int i = 0; i < n; ++i) {
        if (!visited[i]) {
            vector<int> component;
            bfs(i, component, visited, adj);
            components.push_back(component);
        }
    }

    // Step 2: Check if it's possible to partition the components
    // Sort components by size (optional, helps in greedy assignment)
    sort(components.begin(), components.end(), [](const vector<int>& a, const vector<int>& b) {
        return a.size() > b.size();
    });

    vector<int> result(n, 0); // Result array: 1 for A, 2 for B, 3 for C

    // Helper function to assign nodes to a set
    auto assign_nodes = [&](vector<int>& component, int set_id, int count) {
        for (int i = 0; i < component.size() && count > 0; ++i) {
            result[component[i]] = set_id;
            --count;
        }
    };

    // Try assigning nodes greedily
    int remaining_a = a, remaining_b = b, remaining_c = c;

    for (auto& component : components) {
        if (remaining_a >= component.size()) {
            assign_nodes(component, 1, component.size());
            remaining_a -= component.size();
        } else if (remaining_b >= component.size()) {
            assign_nodes(component, 2, component.size());
            remaining_b -= component.size();
        } else if (remaining_c >= component.size()) {
            assign_nodes(component, 3, component.size());
            remaining_c -= component.size();
        } else {
            return vector<int>(n, 0); // No valid partition
        }
    }

    // Check if the partition sizes match
    if (remaining_a == 0 && remaining_b == 0 && remaining_c == 0) {
        return result;
    } else {
        return vector<int>(n, 0); // No valid partition
    }
}

