    vector<vector<int>> graph(n);
    for (int i = 0; i < p.size(); ++i) {
        graph[p[i]].push_back(q[i]);
        graph[q[i]].push_back(p[i]);
    }

    // Step 2: Perform BFS to find connected components
    vector<int> component(n, -1); // component[i] will store the component index of attraction i
    vector<vector<int>> components; // List of attractions in each component
    int component_id = 0;

    for (int i = 0; i < n; ++i) {
        if (component[i] == -1) {
            // Found a new component
            queue<int> q;
            q.push(i);
            component[i] = component_id;
            components.push_back({i});

            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : graph[u]) {
                    if (component[v] == -1) {
                        component[v] = component_id;
                        components.back().push_back(v);
                        q.push(v);
                    }
                }
            }
            ++component_id;
        }
    }

    // Step 3: Check if we can partition attractions into A, B, and C
    // Sort components by size, descending
    vector<pair<int, int>> component_sizes; // {size, component_id}
    for (int i = 0; i < components.size(); ++i) {
        component_sizes.push_back({components[i].size(), i});
    }
    sort(component_sizes.rbegin(), component_sizes.rend());

    // Try to form sets A, B, and C
    vector<int> result(n, 0); // Result array
    vector<int> sizes = {a, b, c};
    vector<int> set_labels = {1, 2, 3}; // Labels for sets A, B, C

    for (int i = 0; i < 3; ++i) {
        int size_needed = sizes[i];
        bool found = false;

        for (auto& [size, comp_id] : component_sizes) {
            if (size >= size_needed) {
                // Assign this component to the current set
                for (int attraction : components[comp_id]) {
                    result[attraction] = set_labels[i];
                }
                // Remove this component from consideration
                size = -1; // Mark as used
                found = true;
                break;
            }
        }

        if (!found) {
            // If we couldn't find a valid partition, return all zeros
            return vector<int>(n, 0);
        }
    }

    // Verify if at least two sets are connected
    int connected_sets = 0;
    for (int label : set_labels) {
        vector<int> positions;
        for (int i = 0; i < n; ++i) {
            if (result[i] == label) {
                positions.push_back(i);
            }
        }

        // Check connectivity of this set
        if (!positions.empty()) {
            queue<int> q;
            vector<bool> visited(n, false);
            q.push(positions[0]);
            visited[positions[0]] = true;

            int count = 1;
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v : graph[u]) {
                    if (!visited[v] && result[v] == label) {
                        visited[v] = true;
                        q.push(v);
                        ++count;
                    }
                }
            }

            if (count == positions.size()) {
                ++connected_sets;
            }
        }
    }

    if (connected_sets >= 2) {
        return result;
    }

    return vector<int>(n, 0);
}

