vector<int> find_split(int n, int a, int b, int c, vector<int> p, vector<int> q) {
    // Create adjacency list for the graph
    vector<vector<int>> adj(n);
    for (int i = 0; i < p.size(); i++) {
        adj[p[i]].push_back(q[i]);
        adj[q[i]].push_back(p[i]);
    }

    // Find connected components using BFS
    vector<int> component(n, -1); // -1 means unvisited
    vector<vector<int>> components; // List of components
    for (int i = 0; i < n; i++) {
        if (component[i] == -1) {
            vector<int> current_component;
            queue<int> q;
            q.push(i);
            component[i] = components.size();
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                current_component.push_back(u);
                for (int v : adj[u]) {
                    if (component[v] == -1) {
                        component[v] = components.size();
                        q.push(v);
                    }
                }
            }
            components.push_back(current_component);
        }
    }

    // Try to assign components to A, B, and C
    vector<int> sizes;
    for (const auto& comp : components) {
        sizes.push_back(comp.size());
    }

    // DP to check if we can form groups of size a, b, and c
    int total_components = components.size();
    vector<vector<bool>> dp_a(total_components + 1, vector<bool>(a + 1, false));
    dp_a[0][0] = true; // Base case: we can achieve size 0 with 0 components

    for (int i = 0; i < total_components; i++) {
        for (int j = 0; j <= a; j++) {
            dp_a[i + 1][j] = dp_a[i][j]; // Don't use this component
            if (j >= sizes[i] && dp_a[i][j - sizes[i]]) {
                dp_a[i + 1][j] = true; // Use this component
            }
        }
    }

    if (!dp_a[total_components][a]) {
        return vector<int>(n, 0); // No valid partition
    }

    vector<vector<bool>> dp_b(total_components + 1, vector<bool>(b + 1, false));
    dp_b[0][0] = true;

    for (int i = 0; i < total_components; i++) {
        for (int j = 0; j <= b; j++) {
            dp_b[i + 1][j] = dp_b[i][j]; // Don't use this component
            if (j >= sizes[i] && dp_b[i][j - sizes[i]]) {
                dp_b[i + 1][j] = true; // Use this component
            }
        }
    }

    if (!dp_b[total_components][b]) {
        return vector<int>(n, 0); // No valid partition
    }

    // Backtrack to find the actual partitions
    vector<int> assignment(n, 0);
    vector<int> used(total_components, 0); // 0 = unassigned, 1 = A, 2 = B, 3 = C

    // Assign to A
    int remaining_a = a;
    for (int i = total_components - 1; i >= 0; i--) {
        if (remaining_a >= sizes[i] && dp_a[i][remaining_a - sizes[i]]) {
            remaining_a -= sizes[i];
            for (int u : components[i]) {
                assignment[u] = 1;
            }
            used[i] = 1; // Mark component as used for A
        }
    }

    // Assign to B
    int remaining_b = b;
    for (int i = total_components - 1; i >= 0; i--) {
        if (used[i] == 0 && remaining_b >= sizes[i] && dp_b[i][remaining_b - sizes[i]]) {
            remaining_b -= sizes[i];
            for (int u : components[i]) {
                assignment[u] = 2;
            }
            used[i] = 2; // Mark component as used for B
        }
    }

    // Assign the rest to C
    for (int i = 0; i < total_components; i++) {
        if (used[i] == 0) {
            for (int u : components[i]) {
                assignment[u] = 3;
            }
        }
    }

    return assignment;
}

