                ++count;
                for (int v : adj[u]) {
                    if (!visited[v] && result[v] == result[u]) {
                        visited[v] = true;
                        q.push(v);
                    }
                }
            }

            if (count == sets[i].size()) {
                ++connected_count;
            }
        }
    }

    if (connected_count >= 2) {
        return result;
    }

    // If not valid, return all zeros
    return vector<int>(n, 0);
}
