// Function to check if there's a valid escape plan for a given time T
bool is_valid_escape_plan(int N, int M, const vector<vector<int>>& adj, const vector<vector<int>>& weights, const vector<int>& exits, int T) {
    vector<int> min_time(N, numeric_limits<int>::max());
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq;

    // Initialize distances for exit chambers
    for (int exit : exits) {
        min_time[exit] = 0;
        pq.emplace(0, exit);
    }

    // Reverse Dijkstra's algorithm
    while (!pq.empty()) {
        int time = pq.top().first;
        int chamber = pq.top().second;
        pq.pop();

        if (time > min_time[chamber]) continue;

        for (size_t i = 0; i < adj[chamber].size(); ++i) {
            int neighbor = adj[chamber][i];
            int weight = weights[chamber][i];

            if (weight > T) continue; // Corridor is too long to use

            int new_time = time + weight;
            if (new_time < min_time[neighbor]) {
                min_time[neighbor] = new_time;
                pq.emplace(new_time, neighbor);
            }
        }
    }

    // Check if chamber 0 can reach any exit within time T
    return min_time[0] <= T;
}

int travel_plan(int N, int M, vector<vector<int>> R, vector<int> L, int K, vector<int> P) {
    // Build adjacency list
    vector<vector<int>> adj(N);
    vector<vector<int>> weights(N);

    for (int i = 0; i < M; ++i) {
        int u = R[i][0];
        int v = R[i][1];
        int weight = L[i];

        adj[u].push_back(v);
        adj[v].push_back(u);
        weights[u].push_back(weight);
        weights[v].push_back(weight);
    }

    // Binary search over T
    int low = 1, high = 1'000'000'000, result = -1;
    while (low <= high) {
        int mid = low + (high - low) / 2;

        if (is_valid_escape_plan(N, M, adj, weights, P, mid)) {
            result = mid;
            high = mid - 1; // Try for a smaller T
        } else {
            low = mid + 1; // Increase T
        }
    }

    return result;
}

