int travel_plan(int N, int M, vector<vector<int>> R, vector<int> L, int K, vector<int> P) {
    // Create adjacency list for the graph
    vector<vector<pair<int, int>>> adj(N);
    for (int i = 0; i < M; ++i) {
        int u = R[i][0], v = R[i][1], w = L[i];
        adj[u].emplace_back(v, w);
        adj[v].emplace_back(u, w);
    }

    // Initialize distances to infinity
    vector<int> dist(N, numeric_limits<int>::max());
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq;

    // Push all exit chambers into the priority queue with distance 0
    for (int exit : P) {
        dist[exit] = 0;
        pq.emplace(0, exit);
    }

    // Dijkstra's algorithm to compute shortest distances to exit chambers
    while (!pq.empty()) {
        auto [d, u] = pq.top();
        pq.pop();

        if (d > dist[u]) continue;

        for (auto [v, w] : adj[u]) {
            if (dist[u] + w < dist[v]) {
                dist[v] = dist[u] + w;
                pq.emplace(dist[v], v);
            }
        }
    }

    // Simulate worst-case escape time for chamber 0
    int max_time = 0;
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq_escape;
    pq_escape.emplace(0, 0); // Start from chamber 0 with time 0

    vector<bool> visited(N, false);
    visited[0] = true;

    while (!pq_escape.empty()) {
        auto [current_time, u] = pq_escape.top();
        pq_escape.pop();

        max_time = max(max_time, current_time);

        vector<pair<int, int>> neighbors;
        for (auto [v, w] : adj[u]) {
            if (!visited[v]) {
                neighbors.emplace_back(v, w);
            }
        }

        // Sort neighbors by their shortest path distances to exit chambers (worst-case blocking)
        sort(neighbors.begin(), neighbors.end(), [&](const pair<int, int>& a, const pair<int, int>& b) {
            return dist[a.first] < dist[b.first];
        });

        // Visit the worst-case neighbor
        for (auto [v, w] : neighbors) {
            visited[v] = true;
            pq_escape.emplace(current_time + w, v);
        }
    }

    return max_time;
}

