int travel_plan(int N, int M, vector<vector<int>> R, vector<int> L, int K, vector<int> P) {
    // Step 1: Build the adjacency list for the graph
    vector<vector<pair<int, int>>> adj(N); // adj[node] = {neighbor, time}
    for (int i = 0; i < M; ++i) {
        int u = R[i][0], v = R[i][1], time = L[i];
        adj[u].emplace_back(v, time);
        adj[v].emplace_back(u, time);
    }

    // Step 2: Multi-source Dijkstra's algorithm
    vector<long long> dist(N, numeric_limits<long long>::max()); // Distance to nearest exit chamber
    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>> pq; // Min-heap

    // Initialize distances for exit chambers
    for (int exit : P) {
        dist[exit] = 0;
        pq.emplace(0, exit);
    }

    // Process the priority queue
    while (!pq.empty()) {
        auto [current_dist, node] = pq.top();
        pq.pop();

        // If we already found a shorter path, skip
        if (current_dist > dist[node]) continue;

        // Relax edges
        for (auto [neighbor, time] : adj[node]) {
            if (dist[node] + time < dist[neighbor]) {
                dist[neighbor] = dist[node] + time;
                pq.emplace(dist[neighbor], neighbor);
            }
        }
    }

    // Step 3: Return the distance for the starting chamber (chamber 0)
    return dist[0];
}

