const int64_t INF = 1e18;

int64_t travel_plan(int N, int M, vector<vector<int>> R, vector<int> L, int K, vector<int> P) {
    // Step 1: Build the graph and reverse graph
    vector<vector<pair<int, int64_t>>> graph(N);
    vector<vector<pair<int, int64_t>>> reverse_graph(N);
    for (int i = 0; i < M; i++) {
        int u = R[i][0], v = R[i][1];
        int64_t w = L[i];
        graph[u].push_back({v, w});
        graph[v].push_back({u, w});
        reverse_graph[v].push_back({u, w});
        reverse_graph[u].push_back({v, w});
    }

    // Step 2: Compute shortest distances to exit chambers using reverse Dijkstra
    vector<int64_t> dist(N, INF);
    priority_queue<pair<int64_t, int>, vector<pair<int64_t, int>>, greater<>> pq;
    for (int exit : P) {
        dist[exit] = 0;
        pq.push({0, exit});
    }

    while (!pq.empty()) {
        auto [d, node] = pq.top();
        pq.pop();
        if (d > dist[node]) continue;
        for (auto [neighbor, weight] : reverse_graph[node]) {
            if (dist[node] + weight < dist[neighbor]) {
                dist[neighbor] = dist[node] + weight;
                pq.push({dist[neighbor], neighbor});
            }
        }
    }

    // Step 3: Binary search to find the minimum T
    int64_t left = 0, right = 1e9, answer = 1e9;
    while (left <= right) {
        int64_t mid = left + (right - left) / 2;

        // Validate if a good escape plan exists for T = mid
        bool is_valid = true;
        for (int chamber = 0; chamber < N; chamber++) {
            if (dist[chamber] > mid) continue; // This chamber cannot reach an exit within T
            int valid_corridors = 0;
            for (auto [neighbor, weight] : graph[chamber]) {
                if (dist[neighbor] <= mid) valid_corridors++;
            }
            if (valid_corridors < 2) {
                is_valid = false;
                break;
            }
        }

        if (is_valid) {
            answer = mid;
            right = mid - 1;
        } else {
            left = mid + 1;
        }
    }

    return answer;
}

