int travel_plan(int N, int M, vector<vector<int>> R, vector<int> L, int K, vector<int> P) {
    // Adjacency list representation of the graph
    vector<vector<pair<int, int>>> graph(N);  // graph[u] = { {v, weight}, ... }

    // Build the graph
    for (int i = 0; i < M; i++) {
        int u = R[i][0];
        int v = R[i][1];
        int weight = L[i];
        graph[u].emplace_back(v, weight);
        graph[v].emplace_back(u, weight);
    }

    // Distance array, initialized to infinity
    vector<long long> dist(N, numeric_limits<long long>::max());
    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>> pq;  // Min-heap

    // Set distances for exit chambers to 0 and push them into the priority queue
    for (int exit : P) {
        dist[exit] = 0;
        pq.emplace(0, exit);
    }

    // Modified Dijkstra's algorithm to calculate shortest distances from exits
    while (!pq.empty()) {
        auto [cur_dist, u] = pq.top();
        pq.pop();

        // If the current distance is already greater, skip
        if (cur_dist > dist[u]) continue;

        // Relax edges
        for (auto [v, weight] : graph[u]) {
            if (dist[v] > cur_dist + weight) {
                dist[v] = cur_dist + weight;
                pq.emplace(dist[v], v);
            }
        }
    }

    // The result is the distance from the starting chamber (0) to the nearest exit
    return dist[0];
}

