int64_t delivery(int N, int K, int L, std::vector<int> positions) {
    // Sort the positions (they are already in non-decreasing order)
    std::sort(positions.begin(), positions.end());

    int64_t total_time = 0;

    // Process the positions in chunks of size K
    for (int i = N - 1; i >= 0; i -= K) {
        // Maximum distance for this trip
        int max_distance = std::min(positions[i], L - positions[i]);
        // Add the round-trip time for this trip
        total_time += 2 * max_distance;
    }

    return total_time;
}

