int64_t delivery(int N, int K, int L, vector<int> positions) {
    // Sort positions (already guaranteed in non-decreasing order by the problem)
    sort(positions.begin(), positions.end());

    int64_t total_time = 0;

    // Process teams in chunks of size K
    for (int i = 0; i < N; i += K) {
        // Find the farthest team in the current chunk
        int farthest = positions[min(i + K - 1, N - 1)];

        // Calculate the time for this trip (clockwise and counterclockwise)
        int64_t clockwise_time = 2 * farthest;                     // Go farthest clockwise and return
        int64_t counterclockwise_time = 2 * (L - farthest);        // Go farthest counterclockwise and return

        // Add the minimum time for this trip to the total time
        total_time += min(clockwise_time, counterclockwise_time);
    }

    return total_time;
}

