int64_t delivery(int N, int K, int L, vector<int> positions) {
    // Split positions into trips and calculate the minimal time
    int trips = (N + K - 1) / K; // Ceiling of N / K
    int64_t total_time = 0;

    // Process positions from the farthest to the nearest
    for (int i = 0; i < trips; ++i) {
        // Determine range of positions covered in this trip
        int start = N - (i + 1) * K; // Start index of this trip
        int end = N - i * K;         // End index (exclusive) of this trip
        start = max(0, start);       // Ensure start is within bounds

        // Farthest position in this trip
        int farthest = positions[end - 1];

        // Calculate the minimum time for this trip
        int64_t clockwise_time = 2 * farthest;                          // Move clockwise to farthest and back
        int64_t counterclockwise_time = 2 * (L - farthest);             // Move counterclockwise to farthest and back
        total_time += min(clockwise_time, counterclockwise_time);       // Add the minimal time for this trip
    }

    return total_time;
}

