// Function to calculate the minimum delivery time
int64_t delivery(int N, int K, int L, vector<int> positions) {
    // Calculate the shortest distance to each team
    vector<int> distances(N);
    for (int i = 0; i < N; ++i) {
        int clockwise = positions[i]; // Distance moving clockwise
        int counterclockwise = L - positions[i]; // Distance moving counterclockwise
        distances[i] = min(clockwise, counterclockwise); // Shortest distance
    }

    // Sort distances (already sorted due to sorted positions)
    // Group teams into trips of size at most K
    int64_t total_time = 0;
    for (int i = N - 1; i >= 0; i -= K) {
        // Farthest distance in this group
        int max_distance = distances[i];
        // Each trip requires going to the farthest point and returning to 0
        total_time += 2 * max_distance;
    }

    return total_time;
}

