int64_t delivery(int N, int K, int L, std::vector<int> positions) {
    // Step 1: Sort positions (already given in non-decreasing order per problem description)
    // Step 2: Divide the delivery into trips, where each trip delivers at most K souvenirs.

    int64_t total_time = 0;

    // Process positions in groups of K
    for (int i = 0; i < N; i += K) {
        // Find the farthest position in this group
        int farthest_position = positions[std::min(i + K - 1, N - 1)];

        // Compute the time for this trip:
        // - Go to the farthest position
        // - Return to section 0
        int64_t trip_time = 2LL * std::min(farthest_position, L - farthest_position);

        // Add the trip time to the total time
        total_time += trip_time;
    }

    return total_time;
}

