// Function to initialize the students' constraints
void init(int N, int A[], int B[]) {
    minTeamSize.assign(A, A + N);
    maxTeamSize.assign(B, B + N);
}

// Function to determine if the required teams can be formed
int can(int M, int K[]) {
    // Count the frequency of required team sizes
    map<int, int> requiredTeams;
    for (int i = 0; i < M; ++i) {
        requiredTeams[K[i]]++;
    }

    // Count the availability of students for each possible team size
    map<int, int> availableStudents;
    for (int i = 0; i < minTeamSize.size(); ++i) {
        for (int size = minTeamSize[i]; size <= maxTeamSize[i]; ++size) {
            availableStudents[size]++;
        }
    }

    // Try to satisfy the required teams using the available students
    for (auto& [teamSize, count] : requiredTeams) {
        if (availableStudents[teamSize] < count) {
            return 0; // Not enough students for this team size
        }
        availableStudents[teamSize] -= count;
    }

    return 1; // All required teams can be formed
}

