vector<int> min_team_size, max_team_size;
map<int, int> team_size_availability;

void init(int N, int A[], int B[]) {
    // Store the min and max team sizes for each student
    min_team_size.resize(N);
    max_team_size.resize(N);
    for (int i = 0; i < N; ++i) {
        min_team_size[i] = A[i];
        max_team_size[i] = B[i];
    }
    // Precompute the availability of team sizes
    team_size_availability.clear();
    for (int i = 0; i < N; ++i) {
        for (int t = min_team_size[i]; t <= max_team_size[i]; ++t) {
            ++team_size_availability[t];
        }
    }
}

int can(int M, int K[]) {
    // Create a copy of the team size availability map
    map<int, int> available = team_size_availability;

    // Sort the required team sizes in descending order to try to fulfill larger teams first
    vector<int> required_team_sizes(K, K + M);
    sort(required_team_sizes.rbegin(), required_team_sizes.rend());

    // Try to fulfill each required team size
    for (int size : required_team_sizes) {
        auto it = available.lower_bound(size);
        if (it == available.end() || it->first < size) {
            // If no suitable team size is available, return 0
            return 0;
        }
        // Decrease the availability of the chosen team size
        if (--it->second == 0) {
            available.erase(it);
        }
    }

    // If all required team sizes are fulfilled, return 1
    return 1;
}

