// Data structure to efficiently count students eligible for specific team sizes
map<int, int> size_count;

// Initialize the data for all students
void init(int N, int A[], int B[]) {
    min_team_size.resize(N);
    max_team_size.resize(N);
    size_count.clear();

    // Populate the constraints for students
    for (int i = 0; i < N; ++i) {
        min_team_size[i] = A[i];
        max_team_size[i] = B[i];
        // Increment the count for all sizes in the range [A[i], B[i]]
        for (int size = A[i]; size <= B[i]; ++size) {
            size_count[size]++;
        }
    }
}

// Check if it's possible to form the required teams for a given day
int can(int M, int K[]) {
    // Create a local copy of size_count to simulate assignment for this query
    map<int, int> available_count = size_count;

    // Sort the required team sizes in descending order
    vector<int> required_sizes(K, K + M);
    sort(required_sizes.rbegin(), required_sizes.rend());

    // Try to satisfy each team size requirement
    for (int size : required_sizes) {
        // Find the smallest available size >= size
        auto it = available_count.lower_bound(size);

        if (it == available_count.end()) {
            // No suitable size available
            return 0;
        }

        // Decrement the count for the chosen size
        it->second--;
        if (it->second == 0) {
            // Remove the size from the map if no students are left
            available_count.erase(it);
        }
    }

    // All team sizes were satisfied
    return 1;
}

