using namespace std;

long long calculate_cost(const vector<int>& X, int left, int right, int median, const vector<long long>& prefix_sum) {
    long long left_sum = prefix_sum[median] - (left > 0 ? prefix_sum[left - 1] : 0);
    long long right_sum = prefix_sum[right] - prefix_sum[median];
    long long median_value = X[median];
    long long left_cost = median_value * (median - left + 1) - left_sum;
    long long right_cost = right_sum - median_value * (right - median);
    return left_cost + right_cost;
}

int besthub(int R, int L, vector<int> X, long long B) {
    vector<long long> prefix_sum(R, 0);
    prefix_sum[0] = X[0];
    for (int i = 1; i < R; ++i) {
        prefix_sum[i] = prefix_sum[i - 1] + X[i];
    }

    int max_fields = 0;
    int left = 0;

    for (int right = 0; right < R; ++right) {
        while (left <= right) {
            int median = (left + right) / 2;
            long long cost = calculate_cost(X, left, right, median, prefix_sum);
            if (cost <= B) {
                max_fields = max(max_fields, right - left + 1);
                break;
            }
            ++left;
        }
    }

    return max_fields;
}

