#include <vector>
#include <algorithm>
#include <numeric>
#include <cmath>
using namespace std;

long long calculate_cost(const vector<int>& X, int left, int right, int median_index) {
    long long median = X[median_index];
    long long cost = 0;

    // Cost for fields to the left of the median
    for (int i = left; i <= median_index; ++i) {
        cost += median - X[i];
    }

    // Cost for fields to the right of the median
    for (int i = median_index + 1; i <= right; ++i) {
        cost += X[i] - median;
    }

    return cost;
}

int besthub(int R, int L, vector<int> X, long long B) {
    vector<long long> prefix_sum(R + 1, 0);
    for (int i = 0; i < R; ++i) {
        prefix_sum[i + 1] = prefix_sum[i] + X[i];
    }

    int max_fields = 0;
    int left = 0;

    for (int right = 0; right < R; ++right) {
        int median_index = (left + right) / 2;
        long long median = X[median_index];

        // Calculate total cost using prefix sums
        long long left_cost = median * (median_index - left + 1) - (prefix_sum[median_index + 1] - prefix_sum[left]);
        long long right_cost = (prefix_sum[right + 1] - prefix_sum[median_index + 1]) - median * (right - median_index);
        long long total_cost = left_cost + right_cost;

        // Check if the cost exceeds the budget
        while (total_cost > B) {
            ++left; // Shrink the window from the left
            median_index = (left + right) / 2;
            median = X[median_index];
            left_cost = median * (median_index - left + 1) - (prefix_sum[median_index + 1] - prefix_sum[left]);
            right_cost = (prefix_sum[right + 1] - prefix_sum[median_index + 1]) - median * (right - median_index);
            total_cost = left_cost + right_cost;
        }

        // Update the maximum number of fields
