    for (int i = 1; i <= R; ++i) {
        prefix_sum[i] = prefix_sum[i - 1] + X[i - 1];
    }

    int max_truckloads = 0;

    // Sliding window approach
    for (int k = 1; k <= R; ++k) {
        for (int start = 0; start + k - 1 < R; ++start) {
            int end = start + k - 1;
            int median_index = (start + end) / 2;

            // Calculate total cost for the window
            long long median = X[median_index];
            long long left_cost = median * (median_index - start + 1) - (prefix_sum[median_index + 1] - prefix_sum[start]);
            long long right_cost = (prefix_sum[end + 1] - prefix_sum[median_index + 1]) - median * (end - median_index);
            long long total_cost = left_cost + right_cost;

            if (total_cost <= B) {
                max_truckloads = max(max_truckloads, k);
            }
        }
    }

    return max_truckloads;
}

