                articulation_points.insert(u);
            }
            if (parent[u] != -1 && low[v] >= discovery[u]) {
                articulation_points.insert(u);
            }
        } else if (v != parent[u]) {
            // Update low value of u for back edge
            low[u] = min(low[u], discovery[v]);
        }
    }
}

