// Initializes the graph with N nodes (rings).
void Init(int N) {
    numRings = N;
    adjList.assign(N, unordered_set<int>());
}

// Links two nodes (rings) in the graph.
void Link(int A, int B) {
    adjList[A].insert(B);
    adjList[B].insert(A);
}

// Helper function to check if a component is a chain.
bool isChain(const unordered_set<int>& component) {
    int endpointCount = 0;
    for (int node : component) {
        int degree = adjList[node].size();
        if (degree > 2) return false;
        if (degree == 1) endpointCount++;
    }
    return endpointCount == 2 || endpointCount == 0; // A chain has 2 endpoints or is a single node.
}

// Performs BFS to find all nodes in a component.
unordered_set<int> getComponent(int start, vector<bool>& visited) {
    unordered_set<int> component;
    queue<int> q;
    q.push(start);
    visited[start] = true;

    while (!q.empty()) {
        int node = q.front();
        q.pop();
        component.insert(node);

        for (int neighbor : adjList[node]) {
            if (!visited[neighbor]) {
                visited[neighbor] = true;
                q.push(neighbor);
            }
        }
    }

    return component;
}

// Counts the number of critical rings.
int CountCritical() {
    int criticalCount = 0;

    for (int i = 0; i < numRings; ++i) {
        // Temporarily remove the node and its edges.
        unordered_map<int, unordered_set<int>> tempEdges;
        for (int neighbor : adjList[i]) {
            tempEdges[neighbor].insert(i);
            adjList[neighbor].erase(i);
        }
        adjList[i].clear();

        // Check the connected components.
        vector<bool> visited(numRings, false);
        bool allChains = true;

        for (int j = 0; j < numRings; ++j) {
            if (!visited[j] && !adjList[j].empty()) {
                auto component = getComponent(j, visited);
                if (!isChain(component)) {
                    allChains = false;
                    break;
                }
            }
        }

        // Restore the node and its edges.
        for (const auto& [neighbor, edges] : tempEdges) {
            for (int edge : edges) {
                adjList[neighbor].insert(edge);
                adjList[edge].insert(neighbor);
            }
        }

        // If the graph becomes all chains, the node is critical.
        if (allChains) {
            criticalCount++;
        }
    }

    return criticalCount;
}

