using namespace std;

// Adjacency list to represent the graph
vector<unordered_set<int>> graph;
// Degree of each node
vector<int> degree;

// Initialize the graph with N nodes
void Init(int N) {
    graph.resize(N);
    degree.resize(N, 0);
}

// Add a link between nodes A and B
void Link(int A, int B) {
    graph[A].insert(B);
    graph[B].insert(A);
    degree[A]++;
    degree[B]++;
}

// Helper function to check if a graph is a valid set of chains
bool isValidChainSet(int N, int exclude) {
    vector<bool> visited(N, false);
    stack<int> s;

    for (int i = 0; i < N; ++i) {
        if (i == exclude || visited[i] || degree[i] == 0) continue;

        // Perform a DFS to check the component connected to node i
        s.push(i);
        visited[i] = true;
        int chainLength = 0; // Length of the current chain
        int endpoints = 0;  // Count of endpoints (nodes with degree <= 1)
        bool isCycle = false;

        while (!s.empty()) {
            int node = s.top();
            s.pop();
            chainLength++;

            int neighbors = 0;
            for (int neighbor : graph[node]) {
                if (neighbor == exclude) continue;
                if (!visited[neighbor]) {
                    s.push(neighbor);
                    visited[neighbor] = true;
                    neighbors++;
                }
            }

            // If a node has more than two neighbors, it's not a chain
            if (neighbors > 2) return false;
            if (neighbors == 1 || neighbors == 0) endpoints++;
            if (neighbors == 0 && chainLength > 1) isCycle = true;
        }

        // For a valid chain, there must be exactly 2 endpoints
        if ((chainLength > 1 && endpoints != 2) || isCycle) return false;
    }

    return true;
}

// Count the number of critical rings
int CountCritical() {
    int N = graph.size();
    int criticalCount = 0;

    for (int i = 0; i < N; ++i) {
        if (degree[i] == 0) {
            // Isolated nodes are always critical
            criticalCount++;
            continue;
        }

        // Temporarily remove node i
        vector<int> neighbors;
        for (int neighbor : graph[i]) {
            neighbors.push_back(neighbor);
            graph[neighbor].erase(i);
            degree[neighbor]--;
        }

        // Check if the remaining graph is a valid set of chains
        if (isValidChainSet(N, i)) {
            criticalCount++;
        }

        // Restore node i
        for (int neighbor : neighbors) {
            graph[neighbor].insert(i);
            degree[neighbor]++;
        }
    }

    return criticalCount;
}

