    vector<vector<int>> adj(n), reverse_adj(n);
    vector<int> out_degree(n, 0); // Out-degree for each node

    // Build the graph and reverse graph
    for (int i = 0; i < m; i++) {
        adj[u[i]].push_back(v[i]);
        reverse_adj[v[i]].push_back(u[i]);
        out_degree[u[i]]++;
    }

    // Winning state for each station: 1 = Arezou wins, 0 = Borzou wins
    vector<int> win(n, 0);
    queue<int> q;

    // Initialize the queue with all charging stations
    for (int i = 0; i < n; i++) {
        if (r[i] == 1) {
            win[i] = 1; // Charging stations are winning states
            q.push(i);
        }
    }

    // Propagate winning states in reverse graph
    while (!q.empty()) {
        int curr = q.front();
        q.pop();

        for (int prev : reverse_adj[curr]) {
            if (win[prev] == 1) continue; // Already determined as winning

            if (a[prev] == 1) {
                // Arezou's station: If any outgoing leads to a win, she wins
                win[prev] = 1;
                q.push(prev);
            } else {
                // Borzou's station: All outgoing must lead to losing for her to lose
                out_degree[prev]--;
                if (out_degree[prev] == 0) {
                    win[prev] = 1;
                    q.push(prev);
                }
            }
        }
    }

    return win;
}

