    for (int i = 0; i < n; ++i) {
        out_degree[i] = graph[i].size();
    }

    // Queue for processing stations
    queue<int> q;

    // Initialize the queue with charging stations
    for (int i = 0; i < n; ++i) {
        if (r[i] == 1) { // Charging station
            can_win[i] = 1;
            q.push(i);
        }
    }

    // Process stations in reverse topological order
    while (!q.empty()) {
        int station = q.front();
        q.pop();

        for (int prev_station : reverse_graph[station]) {
            if (a[prev_station] == 1) { // Arezou owns the station
                if (can_win[prev_station] == 0) { // Not yet marked as winning
                    can_win[prev_station] = 1;
                    q.push(prev_station);
                }
            } else { // Borzou owns the station
                out_degree[prev_station]--;
                if (out_degree[prev_station] == 0 && can_win[prev_station] == 0) {
                    can_win[prev_station] = 1;
                    q.push(prev_station);
                }
            }
        }
    }

    // Determine the result for each starting station
    for (int i = 0; i < n; ++i) {
        result[i] = can_win[i];
    }

