// Helper function to perform Tarjan's SCC algorithm
void tarjan(int node, int& index, vector<int>& indices, vector<int>& lowLink, stack<int>& stk, vector<bool>& onStack, const vector<vector<int>>& adj, vector<vector<int>>& sccs) {
    indices[node] = lowLink[node] = index++;
    stk.push(node);
    onStack[node] = true;

    for (int neighbor : adj[node]) {
        if (indices[neighbor] == -1) {
            tarjan(neighbor, index, indices, lowLink, stk, onStack, adj, sccs);
            lowLink[node] = min(lowLink[node], lowLink[neighbor]);
        } else if (onStack[neighbor]) {
            lowLink[node] = min(lowLink[node], indices[neighbor]);
        }
    }

    if (lowLink[node] == indices[node]) {
        vector<int> scc;
        int member;
        do {
            member = stk.top();
            stk.pop();
            onStack[member] = false;
            scc.push_back(member);
        } while (member != node);
        sccs.push_back(scc);
    }
}

