        if (indices[i] == -1) {
            tarjan(i, index, indices, lowLink, stk, onStack, adj, sccs);
        }
    }

    // Determine if each SCC has a charging station
    vector<bool> sccHasCharging;
    vector<int> nodeToSCC(n, -1);
