        int scc = nodeToSCC[i];
        if (sccHasCharging[scc]) {
            canWin[i] = true;
        }
    }

    // Propagate winning statuses
    for (int i = 0; i < n; ++i) {
        if (canWin[i]) {
            result[i] = 1;
        } else {
            result[i] = 0;
        }
    }

    return result;
}

